//Interface
    private void jButtonConvertActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonConvertActionPerformed
        try {
            //เรียกใช้ Accessor Method
            double input = Double.parseDouble(jTextField1.getText());
            String fromUnit = (String) jComboBoxFrom.getSelectedItem();
            String toUnit = (String) jComboBoxTo.getSelectedItem();
            //เรียกใช้ Method convertFrom และ convertTo สำหรับแปลงค่า
            double Answer = convertFromUnit(input, fromUnit);
            double result = convertToUnit(Answer, toUnit);
            jLabelOutput.setText(String.format("%,.3f %s", result, toUnit)); //ส่งออกค่าเป็นจำนวนทศนิยมไม่เกิน 3 ตำแหน่ง
        } catch (NumberFormatException e) { //ดัก Error เวลากรอกค่าผิด
            JOptionPane.showMessageDialog(this, "Invalid input value", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_jButtonConvertActionPerformed

