    private double convertFromUnit(double value, String unit) {
        return switch (unit) {
            case "Nanometers" -> value / 1000000000;
            case "Micrometers" -> value / 1000000;
            case "Millimeters" -> value / 1000;
            case "Centimeters" -> value / 100;
            case "Kilometers" -> value * 1000;
            default -> value;
        };
    }
    private double convertToUnit(double value, String unit) {
        return switch (unit) {
            case "Nanometers" -> value * 1000000000;
            case "Micrometers" -> value * 1000000;
            case "Millimeters" -> value * 1000;
            case "Centimeters" -> value * 100;
            case "Kilometers" -> value / 1000;
            default -> value;
        };
    }
